  const [qqValue, setQq] = useState(0)
  const [xValue, setX] = useState(0)
  const [xlcValue, setXlc] = useState(0)
  const [sValue, setS] = useState(0)

  const [ietOldValue, setIetOld] = useState(0)
  const [nOldValue, setNOld] = useState(0)
  const [zhtOldValue, setZhtOld] = useState(0)
  const [vOldValue, setVOld] = useState(0)
  const [elyOldValue, setElyOld] = useState(0)
  const [tzwOldValue, setTzwOld] = useState(0)
  const [fikOldValue, setFikOld] = useState(0)
  const [tOldValue, setTOld] = useState(0)
  const [zqOldValue, setZqOld] = useState(0)
  const [npOldValue, setNpOld] = useState(0)
  const [mjOldValue, setMjOld] = useState(0)
  const [kgOldValue, setKgOld] = useState(0)
  const [oyOldValue, setOyOld] = useState(0)
  const [intOldValue, setIntOld] = useState(0)
  const [obOldValue, setObOld] = useState(0)
  const [acmOldValue, setAcmOld] = useState(0)
  const [qqOldValue, setQqOld] = useState(0)
  const [xOldValue, setXOld] = useState(0)
  const [xlcOldValue, setXlcOld] = useState(0)
  const [sOldValue, setSOld] = useState(0)


  const stocks = [
    { key: 'IET', value: ietValue, oldValue: ietOldValue, color: 'green' },
    { key: 'N', value: nValue, oldValue: nOldValue, color: 'green'  },
    { key: 'ZHT', value: zhtValue, oldValue: zhtOldValue, color: 'green'  },
    { key: 'V', value: vValue, oldValue: vOldValue, color: 'green'  },
    { key: 'ELY', value: elyValue, oldValue: elyOldValue, color: 'green'  },
    { key: 'TZW', value: tzwValue, oldValue: tzwOldValue, color: 'green'  },
    { key: 'FIK', value: fikValue, oldValue: fikOldValue, color: 'green'  },
    { key: 'T', value: tValue, oldValue: tOldValue, color: 'green'  },
    { key: 'ZQ', value: zqValue, oldValue: zqOldValue, color: 'green'  },
    { key: 'NP', value: npValue, oldValue: npOldValue, color: 'green'  },
    { key: 'MJ', value: mjValue, oldValue: mjOldValue, color: 'green'  },
    { key: 'KG', value: kgValue, oldValue: kgOldValue, color: 'green'  },
    { key: 'OY', value: oyValue, oldValue: oyOldValue, color: 'green'  },
    { key: 'ITN', value: intValue, oldValue: intOldValue, color: 'green'  },
    { key: 'OB', value: obValue, oldValue: obOldValue, color: 'green'  },
    { key: 'ACM', value: acmValue, oldValue: acmOldValue, color: 'green'  },
    { key: 'QQ', value: qqValue, oldValue: qqOldValue, color: 'green'  },
    { key: 'X', value: xValue, oldValue: xOldValue, color: 'green'  },
    { key: 'XLC', value: xlcValue, oldValue: xlcOldValue, color: 'green'  },
    { key: 'S', value: sValue, oldValue: sOldValue, color: 'green'  }
  ]

  function createWS() {
    const msg = {
      stocks: ['IET', 'N', 'ZHT', 'V', 'ELY', 'TZW', 'FIK', 'T', 'ZQ', 'NP', 'MJ', 'KG', 'OY', 'ITN', 'OB', 'ACM', 'QQ', 'X', 'XLC', 'S'], event: 'subscribe'
    }
    const webSocket = new WebSocket('ws://localhost:8080');
    webSocket.onerror = (error) => {
      console.log('WebSocket Error: ' + error);
    };

    webSocket.onclose = (event) => {
      console.log('Disconnected from WebSocket.', event);
      setTimeout(function () { createWS() }, 3000); // 3 segundos
    }

    webSocket.onopen = (event) => {
      webSocket.send(JSON.stringify(msg))
    };

    webSocket.onmessage = (event) => {
      var data = JSON.parse(event.data)
      var key = ''
      var newValue = 0
      if (data.stocks !== undefined) {
        key = Object.keys(data.stocks)
        newValue = Object.values(data.stocks)
      }
      setTeste(JSON.stringify(data.stocks))
      if (key[0] === 'IET') {
        setIetOld(ietValue)
        setIet(Number(newValue))
      }
      if (key[0] === 'N') {
        setNOld(nValue)
        setN(Number(newValue))
      }
      if (key[0] === 'ZHT') {
        setZhtOld(zhtValue)
        setZht(Number(newValue))
      }
      if (key[0] === 'V') {
        setVOld(vValue)
        setV(Number(newValue))
      }
      if (key[0] === 'ELY') {
        setElyOld(elyValue)
        setEly(Number(newValue))
      }
      if (key[0] === 'TZW') {
        setTzwOld(tzwValue)
        setTzw(Number(newValue))
      }
      if (key[0] === 'FIK') {
        setFikOld(fikValue)
        setFik(Number(newValue))
      }
      if (key[0] === 'T') {
        setTOld(tValue)
        setT(Number(newValue))
      }
      if (key[0] === 'ZQ') {
        setZqOld(zqValue)
        setZq(Number(newValue))
      }
      if (key[0] === 'NP') {
        setNpOld(npValue)
        setNp(Number(newValue))
      }
      if (key[0] === 'MJ') { 
        setMjOld(mjValue)
        setMj(Number(newValue))
       }
      if (key[0] === 'KG') {
         setKgOld(kgValue)
         setKg(Number(newValue))
         }
      if (key[0] === 'OY') { 
        setOyOld(oyValue)
        setOy(Number(newValue))
       }
      if (key[0] === 'ITN') { 
        setIntOld(intValue)
        setInt(Number(newValue)) 
      }
      if (key[0] === 'OB') { 
        setObOld(obValue)
        setOb(Number(newValue)) 
      }
      if (key[0] === 'ACM') {
         setAcmOld(acmValue)
         setAcm(Number(newValue))
         }
      if (key[0] === 'QQ') {
         setQqOld(qqValue)
         setQq(Number(newValue))
         }
      if (key[0] === 'X') { 
        setXOld(xValue)
        setX(Number(newValue))
       }
      if (key[0] === 'XLC') { 
        setXlcOld(xlcValue)
        setXlc(Number(newValue))
       }
      if (key[0] === 'S') { 
        setSOld(sValue)
        setS(Number(newValue)) 
      }
    }
  }

  useEffect(() => {
    createWS()
  }, [])

  function mask(value) {
    return '$ ' + value.toFixed(2)
  }
  const [classe, setClass] = useState('class1')
  return (

    <div>
      <div className='container d-flex justify-content-center row acoes mt-4'>
        {stocks && stocks.map((stock, index) => {

          return (
            /* <Acao acao={stock} key={index} />*/
            < div className="col-md-2 card m-2" >
              <h2 className='title-card mt-2 m-1 d-flex justify-content-center'> {stock.key}</h2>
              <h3 id='value' className='text-center pt-2 text-success'>{mask(stock.value)}</h3>
              <h4 className='text-center'>{}</h4>
            </div>
          )
        })}
      </div>
    </div>

  )
}

export default Acoes
