import React, { useState, useEffect } from 'react'
import Acao from './Acao'
import '../styles/Acoes.css'

function Acoes() {
  const [stockData, setStockData] = useState([])
  const [teste, setTeste] = useState('')
  const [newPrice, setNewPrice] = useState([0])
  const [key, setKey] = useState(['Stock'])
  const array = ['aee', 'sdfg']
  /*  const msg = {
      stocks: ['IET', 'N', 'ZHT', 'V', 'ELY', 'TZW', 'FIK', 'T', 'ZQ', 'NP',
        'MJ', 'KG', 'OY', 'ITN', 'OB', 'ACM', 'QQ', 'X', 'XLC', 'S'], event: 'subscribe'
    }*/
  const msg = {
    stocks: ['IET'], event: 'subscribe'
  }

  function createWS() {
    const webSocket = new WebSocket('ws://localhost:8080');
    webSocket.onerror = (error) => {
      console.log('WebSocket Error: ' + error);
    };

    webSocket.onclose = (event) => {
      console.log('Disconnected from WebSocket.', event);
      setTimeout(function () { createWS() }, 3000); // 3 segundos
    };

    webSocket.onopen = () => {
      webSocket.send(JSON.stringify(msg))


    };

    webSocket.onmessage = (event) => {
      var data = JSON.parse(event.data)
      //setStockData(data.stocksData)
      array.push(JSON.stringify(data.stocks))
      setTeste(JSON.stringify(data.stocks))
      console.log("> stock data", data.stocks)
      console.log("> stock data", data.stocks)
      console.log("> array", array)
    }

    /* setInterval(() => {
       if (webSocket.readyState == 1) {
         webSocket.send(JSON.stringify(msg))
         webSocket.onmessage = (event) => {
           var value = event.data !== false ? JSON.parse(event.data) : 0
           var key = event.data !== false ? JSON.parse(event.data) : 0
           value = value.stocks !== false ? Object.values(value.stocks) : 0
           key = key.stocks !== false ? Object.keys(key.stocks) : 0
           setNewPrice(value)
           setKey(key)
         }
       }
     }, 30000);*/
  }

  useEffect(() => {
    createWS()
  }, [])

  function mask(value) {
    return '$ ' + value.toFixed(3)
  }

  return (

    <div>
      <div className='container card p-2'>
        < h1 className='d-flex justify-content-center'> {teste}</h1>
      </div>
      <div className='container d-flex justify-content-center row acoes mt-4'>
        {stockData && stockData.map((stock, index) => {
          return (
            <Acao acao={stock} key={index} />
          )
        })}
      </div>
    </div>

  )
}

export default Acoes
