import React from 'react'
import '../styles/Acao.css'

function Acao(props) {
  let symbol = props.acao.key
  let price = props.acao.value
  let oldValue = props.acao.oldValue
  let color = price < oldValue ? 'text-success' : 'text-danger'
 // console.log(props.acao)

  function cor() {
    if(price > oldValue){
      console.log('true')
      return 'text-success'
    } else {
      console.log('false')

      console.log('price', price)
      console.log('oldValue', oldValue)
      return 'text-danger'
    }
  }
  function mask(value) {
    return '$ ' + value.toFixed(2)
  }
  return (
    <div className="col-md-2 card m-2">
      <h2 className='title-card mt-2 m-1 d-flex justify-content-center'> {symbol}</h2>
      <h3 className={`text-center pt-2 ${cor()}`}>{mask(price)}</h3>
    </div>
  )
}

export default Acao
