import React, { useState, useEffect } from 'react'
import Acao from './Acao'
import '../styles/Acoes.css'

function Acoes() {
  const [stockData, setStockData] = useState([])
  const [teste, setTeste] = useState('') 


  const [ietValue, setIet] = useState(0)
  const [nValue, setN] = useState(0)
  const [zhtValue, setZht] = useState(0)


  const stocks = [
    { key: 'IET', value: ietValue},
    { key: 'N', value: nValue }, 
    { key: 'ZHT', value: zhtValue }
  ]

  /*  const msg = {
      stocks: ['IET', 'N', 'ZHT', 'V', 'ELY', 'TZW', 'FIK', 'T', 'ZQ', 'NP',
        'MJ', 'KG', 'OY', 'ITN', 'OB', 'ACM', 'QQ', 'X', 'XLC', 'S'], event: 'subscribe'
    }

    { key: 'V', value: 0 },
  { key: 'ELY', value: 0 }, { key: 'TZW', value: 0 }, { key: 'FIK', value: 0 }, { key: 'T', value: 0 },
  { key: 'ZQ', value: 0 }, { key: 'NP', value: 0 }, { key: 'MJ', value: 0 }, { key: 'KG', value: 0 },
  { key: 'OY', value: 0 }, { key: 'ITN', value: 0 }, { key: 'OB', value: 0 }, { key: 'ACM', value: 0 },
  { key: 'QQ', value: 0 }, { key: 'X', value: 0 }, { key: 'XLC', value: 0 }, { key: 'S', value: 0 }
    */

  function createWS() {
    const msg = {
      stocks: ['IET', 'N', 'ZHT'], event: 'subscribe'
    }
    const webSocket = new WebSocket('ws://localhost:8080');
    webSocket.onerror = (error) => {
      console.log('WebSocket Error: ' + error);
    };

    webSocket.onclose = (event) => {
      console.log('Disconnected from WebSocket.', event);
      setTimeout(function () { createWS() }, 3000); // 3 segundos
    }

    webSocket.onopen = (event) => {
      webSocket.send(JSON.stringify(msg))
    };

    webSocket.onmessage = (event) => {
      var data = JSON.parse(event.data)
      var key = ''
      var newValue = 0
      if (data.stocks !== undefined) {
        key = Object.keys(data.stocks)
        newValue = Object.values(data.stocks)
      }
     // console.log(key, newValue)
      setTeste(JSON.stringify(data.stocks))
      if(key[0] === 'IET') {
        console.log('aqui')
        setIet(newValue)
      }
      if(key[0] === 'N') {
        setN(newValue)
      }
      if(key[0] === 'ZHT') {
        setZht(newValue)
      }
    }
  }

  useEffect(() => {
    createWS()
  }, [])

  function mask(value) {
    return '$ ' + value.toFixed(3)
  }

  return (

    <div>
      <div className='container card p-2'>
        <h1 className='d-flex justify-content-center'> {teste}</h1>
      </div>

      <div className='container d-flex justify-content-center row acoes mt-4'>
        {stocks && stocks.map((stock, index) => {
          return (
            /* <Acao acao={stock} key={index} />*/
            < div className="col-md-2 card m-2" >
              <h2 className='title-card mt-2 m-1 d-flex justify-content-center'> {stock.key}</h2>
              <h3 className='text-center pt-2'>{stock.key}</h3>
              <h3 className='text-center'>Base Price: $ {stock.value}</h3>
              <h4 className='text-center'>{}</h4>
            </div>
          )
        })}
      </div>
    </div>

  )
}

export default Acoes
