import React, { useState, useEffect } from 'react'
import Acao from './Acao'
import '../styles/Acoes.css'

function Acoes() {
  const webSocket = new WebSocket('ws://localhost:8080');
  const [stockData, setStockData] = useState([])
  const [newPrice, setNewPrice] = useState(0)

  //const msg = { stocks: ['IET', 'N', 'ZHT', 'V', 'ELY', 'TZW', 'FIK', 'T', 'ZQ', 'NP', 
  //'MJ', 'KG', 'OY', 'ITN', 'OB', 'ACM', 'QQ', 'X', 'XLC', 'S'], event: 'subscribe' }
  const msg = { stocks: ['IET', 'OB'], event: 'subscribe' }

  useEffect(() => {
    webSocket.onopen = () => {
      webSocket.send(JSON.stringify(msg))
    };

    webSocket.onmessage = (event) => {
      var data = JSON.parse(event.data)
      setStockData(data.stocksData)
    }
  }, [])

  function atualPrice(symbol) {
    console.log( 'aquiuihiuhui')
    if (webSocket.readyState == 1) {
      webSocket.send(JSON.stringify({ stocks: [symbol], event: 'subscribe' }))
      webSocket.onmessage = (event) => {
        var data = JSON.parse(event.data)
        console.log('>>>>> 1', data)
        data = Object.values(data.stocks)
        //newPrice.push(data)
        setNewPrice(data)
        console.log('>>>>> 2', data)
        //console.log('>>', data.stocks.getValue())
        //setStockData(data.stocksData)
      }
    }
  }
  /*  setInterval(() => {
      if (webSocket.readyState == 1) {
        console.log('awui dentro')
        webSocket.send(JSON.stringify(msg))
        webSocket.onmessage = (event) => {
          var data = JSON.parse(event.data)
          console.log('>>>>> 1', data)
          data = Object.values(data.stocks)
          //newPrice.push(data)
          //setNewPrice(data)
          console.log('>>>>> 2', data)
          //console.log('>>', data.stocks.getValue())
          //setStockData(data.stocksData)
        }
      }
    }, 6000);*/

  webSocket.onerror = (error) => {
    console.log('WebSocket Error: ' + error);
  };

  webSocket.onclose = (event) => {
    console.log('Disconnected from WebSocket.', event);
  };

  return (
    <div className='container d-flex justify-content-center row acoes'>
      {stockData && stockData.map((stock, index) => {
        console.log(">", stock)
        console.log(">>", newPrice)
        atualPrice(stock.symbol)
        return (
          <Acao acao={stock} price={newPrice} key={index} />
        )
      })}
    </div>
  )
}

export default Acoes
